### collect_ichorCNA_output.R ######################################################################
# Finds and combines output generated by ichorCNA

### FUNCTIONS ######################################################################################
# function to generate a standardized filename
generate.filename <- function(project.stem, file.core, extension, include.date = TRUE) {

	# build up the filename
	file.name <- paste(project.stem, file.core, sep = '_');
	file.name <- paste(file.name, extension, sep = '.');

	if (include.date) {
		file.name <- paste(Sys.Date(), file.name, sep = '_');
		}

	return(file.name);
	}

# function to write session profile to file
save.session.profile <- function(file.name) {

	# open the file
	sink(file = file.name, split = FALSE);

	# write memory usage to file
	cat('### MEMORY USAGE ###############################################################');
	print(proc.time());

	# write sessionInfo to file
	cat("\n### SESSION INFO ###############################################################");
	print(sessionInfo());

	# close the file
	sink();

	}

### PREPARE SESSION ################################################################################
# import libraries
library(argparse);

# import command line arguments
parser <- ArgumentParser();

parser$add_argument('-d', '--directory', type = 'character', help = 'path to data directory');
parser$add_argument('-p', '--project', type = 'character', help = 'project name');

arguments <- parser$parse_args();

# what's the date?
date <- Sys.Date();

setwd(arguments$directory);

### MAIN ###########################################################################################
# find results files
metrics.files <- list.files(pattern = 'final_metrics.txt', recursive = TRUE);
cn.files <- list.files(pattern = 'cna.seg', recursive = TRUE);

# read them in
metric.data <- do.call(rbind,
	lapply(
		metrics.files,
		read.delim
		)
	);

cnData <- list();
for (file in cn.files) {
	# extract sample ID
	smp <- sub('.cna.seg','',basename(file));
	# store data in list
	tmp <- read.delim(file);
	if (nrow(tmp) > 0) {
		tmp$Sample <- smp;
		colnames(tmp) <- gsub(paste0(smp, '.'),'',colnames(tmp));
		cnData[[smp]] <- tmp;
		}
	}

# reshape/format data
cn.calls <- do.call(rbind, cnData);

# save combined/formatted data to file
write.table(
	metric.data,
	file = generate.filename(arguments$project, 'ichorCNA_estimates','tsv'),
	row.names = FALSE,
	col.names = TRUE,
	sep = '\t'
	);

write.table(
	cn.calls,
	file = generate.filename(arguments$project, 'perbin_cna_status','tsv'),
	row.names = FALSE,
	col.names = TRUE,
	sep = '\t'
	);

### SAVE SESSION INFO ##############################################################################
save.session.profile(generate.filename('CollectIchorCNA','SessionProfile','txt'));
