### collect_msi_estimates.R ########################################################################
# Finds and combines output generated by MSI-Sensor

### FUNCTIONS ######################################################################################
# function to generate a standardized filename
generate.filename <- function(project.stem, file.core, extension, include.date = TRUE) {

	# build up the filename
	file.name <- paste(project.stem, file.core, sep = '_');
	file.name <- paste(file.name, extension, sep = '.');

	if (include.date) {
		file.name <- paste(Sys.Date(), file.name, sep = '_');
		}

	return(file.name);
	}

# function to write session profile to file
save.session.profile <- function(file.name) {

	# open the file
	sink(file = file.name, split = FALSE);

	# write memory usage to file
	cat('### MEMORY USAGE ###############################################################');
	print(proc.time());

	# write sessionInfo to file
	cat("\n### SESSION INFO ###############################################################");
	print(sessionInfo());

	# close the file
	sink();

	}

### PREPARE SESSION ################################################################################
# import libraries
library(argparse);

# import command line arguments
parser <- ArgumentParser();

parser$add_argument('-d', '--directory', type = 'character', help = 'path to data directory');
parser$add_argument('-p', '--project', type = 'character', help = 'project name');

arguments <- parser$parse_args();

# what's the date?
date <- Sys.Date();

setwd(arguments$directory);

### MAIN ###########################################################################################
# find results files
msi.files <- list.files(pattern = '_output$', recursive = TRUE);
somatic.files <- list.files(pattern = '_somatic$', recursive = TRUE);

# read them in
msi.list <- list();
somatic.list <- list();

for (file in msi.files) {
	# extract sample ID
	smp <- sub('_msi_output$','',basename(file));
	# store data in list
	msi.list[[smp]] <- read.delim(file);
	}

for (file in somatic.files) {
	# extract sample ID
	smp <- sub('_msi_output_somatic','',basename(file));
	# store data in list
	tmp <- read.delim(file);
	if (nrow(tmp) > 0) {
		tmp$Sample <- smp;
		somatic.list[[smp]] <- tmp;
		}
	}

# reshape/format data
msi.data <- do.call(rbind, msi.list);
colnames(msi.data)[3] <- 'Percent';

somatic.data <- do.call(rbind, somatic.list);

# save combined/formatted data to file
write.table(
	msi.data,
	file = generate.filename(arguments$project, 'msi_estimates','tsv'),
	row.names = TRUE,
	col.names = NA,
	sep = '\t'
	);

write.table(
	somatic.data,
	file = generate.filename(arguments$project, 'somatic_msi_sites','tsv'),
	row.names = FALSE,
	col.names = TRUE,
	sep = '\t'
	);

### SAVE SESSION INFO ##############################################################################
save.session.profile(generate.filename('CollectMSI','SessionProfile','txt'));
