### collect_sequencing_metrics.R ###################################################################
# Finds and combines output generated by picard/gatk4

### FUNCTIONS ######################################################################################
# function to generate a standardized filename
generate.filename <- function(project.stem, file.core, extension, include.date = TRUE) {

	# build up the filename
	file.name <- paste(project.stem, file.core, sep = '_');
	file.name <- paste(file.name, extension, sep = '.');

	if (include.date) {
		file.name <- paste(Sys.Date(), file.name, sep = '_');
		}

	return(file.name);
	}

# function to write session profile to file
save.session.profile <- function(file.name) {

	# open the file
	sink(file = file.name, split = FALSE);

	# write memory usage to file
	cat('### MEMORY USAGE ###############################################################');
	print(proc.time());

	# write sessionInfo to file
	cat("\n### SESSION INFO ###############################################################");
	print(sessionInfo());

	# close the file
	sink();

	}

### PREPARE SESSION ################################################################################
# import libraries
library(argparse);

# import command line arguments
parser <- ArgumentParser();

parser$add_argument('-d', '--directory', type = 'character', help = 'path to data directory');
parser$add_argument('-p', '--project', type = 'character', help = 'project name');

arguments <- parser$parse_args();

# what's the date?
date <- Sys.Date();

setwd(arguments$directory);

### MAIN ###########################################################################################
# find results files
detail.files <- list.files(pattern = 'pre_adapter_detail_metrics', recursive = TRUE);
summary.files <- list.files(pattern = 'pre_adapter_summary_metrics', recursive = TRUE);
contamination.files <- list.files(pattern = 'contamination.table', recursive = TRUE);

# read them in
artefact.list <- list();
contamination.list <- list();

for (file in detail.files) {
	# extract sample ID
	smp <- sub('_artifact_metrics.txt.pre_adapter_detail_metrics','',basename(file));
	# store data in list
	detailed <- read.delim(file, comment.char = '#');
	summarized <- read.delim(sub('detail','summary',file), comment.char = '#');

	summarized <- summarized[!is.na(summarized$ARTIFACT_NAME),];
	tmp <- merge(summarized[,c(1:4,12)], detailed);

	artefact.list[[smp]] <- tmp;
	rm(detailed,summarized,tmp);
	}

for (file in contamination.files) {
	# extract sample ID
	smp <- sub('_contamination.table','',basename(file));
	# store data in list
	contamination.list[[smp]] <- read.delim(file);
	}

# reshape/format data
artefact.data <- do.call(rbind, artefact.list);
contest.data <- do.call(rbind, contamination.list);

# format data
artefact.data <- artefact.data[,c('SAMPLE_ALIAS','REF_BASE','ALT_BASE','ARTIFACT_NAME','CONTEXT','QSCORE')];

# save combined/formatted data to file
write.table(
	artefact.data,
	file = generate.filename(arguments$project, 'SequenceArtefacts','tsv'),
	row.names = FALSE,
	col.names = TRUE,
	sep = '\t'
	);

write.table(
	contest.data,
	file = generate.filename(arguments$project, 'ContaminationEstimates','tsv'),
	row.names = FALSE,
	col.names = TRUE,
	sep = '\t'
	);

### SAVE SESSION INFO ##############################################################################
save.session.profile(generate.filename('CollectArtefacts','SessionProfile','txt'));
